import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-hijo',
  templateUrl: './hijo.component.html',
  styleUrls: ['./hijo.component.css']
})

export class HijoComponent {
  nombreHijo:string='Javier';
  //Este mensaje se recibe desde el padre
  @Input() apePaternoHijo:string ='';
  @Input() apeMaternoHijo:string='';
}
<div class="datosHijo">
  <h2>Datos del Hijo</h2>
  <span>Nombre:</span><b>{{ nombreHijo }}</b><br/>
  <span>Apellido Paterno:</span><b>{{ apePaternoHijo }}</b><br/>
  <span>Apellido Materno:</span><b>{{ apeMaternoHijo }}</b><br/>
</div>
